(1, 'Update Windows', 18, 'user', 5, 0, 'Update Windows untuk keamanan data', '<ol>\r\n	<li>Klik Setting/pengaturan</li>\r\n	<li>pilih Update &amp; Security</li>\r\n	<li>Pilih Chek Update / Download / restart now</li>\r\n	<li>tunggu sampai update selesai</li>\r\n	<li>restart laptop</li>\r\n	<li>selesai</li>\r\n</ol>', 2, NULL, 0, '11:30:00', '11:30:00', '2021-04-21 04:33:27'),
(2, 'Setting PABX', 20, 'user', 5, 0, '1. Telepon tidak menyala\r\n2. Telepon Tidak bisa di gunakan\r\n3. Telepon Error', '<ol>\r\n	<li>Periksa Konektor Power yang ada di samping PABX</li>\r\n	<li>Periksa Konektor LAN yang ada di dalamm PABX</li>\r\n	<li>Pastikan Konektor terpasang dengan benar</li>\r\n	<li>Selesai</li>\r\n</ol>', 2, NULL, 0, '09:15:00', '11:15:00', '2021-04-28 03:08:30'),
(3, 'Checklist Jaringan Wifi', 20, 'user', 5, 0, 'Wifi Lemot, Wifi Tidak bisa diakses', '<ol>\r\n	<li>Pastikan Router Menyala</li>\r\n	<li>Periksa lampu indikator Wifi</li>\r\n	<li>Reset Router Wifi</li>\r\n	<li>Nyalakan kembali Router</li>\r\n	<li>Selesai</li>\r\n</ol>', 2, NULL, 4, '10:15:00', '10:15:00', '2021-04-28 03:10:44'),
(4, 'Checklist CCTV', 20, 'user', 5, 0, 'layar tidak tampil, Mengganti saluran CCTV', '<ol>\r\n	<li>Buka Pola CCTV</li>\r\n	<li>Pilih Setting</li>\r\n	<li>Pilih Live View</li>\r\n	<li>Pilih Video Output : VGA/HDMI (Ruangan IT dan Ruangan Pak Okta), Aux CVBS/Channel-Zero (Security)</li>\r\n	<li>kemudian Pillih Camera yang ingin di tampilkan atau camera yang bermasalah</li>\r\n	<li>Pilih Apply</li>\r\n	<li>Selesai</li>\r\n</ol>', 2, NULL, 1, '10:15:00', '10:15:00', '2021-04-28 03:16:28'),
(5, 'Checklist Software', 20, 'user', 5, 0, 'Pergantian Laptop, Maintenance Lapotop, Penerimaan dan Pengembalian Laptop', '<ol>\r\n	<li>Microsoft Office</li>\r\n	<li>Anti virus</li>\r\n	<li>PDF Reader</li>\r\n	<li>Email</li>\r\n	<li>Akses Internet</li>\r\n	<li>Akses Printer</li>\r\n	<li>Software Printer</li>\r\n	<li>Update Windows</li>\r\n</ol>', 2, NULL, 9, '10:30:00', '10:30:00', '2021-04-28 03:18:56'),
(6, 'MS OFFICE 365 MENDADAK TIDAK BISA DIPAKAI', 7, 'user', 5, 0, '1.MS OFFICE MENDADAK TIDAK BERFUNGSI\r\n2.DI INSTAL KEMBALI TIDAK BISA TERPAKAI/TIDAK KOMPLET\r\n3.ADA TULISAN (this operation has been cancelled due to restrictions in effect on this computer) SAAT RE INSTAL', '<p>1.Unisntal microsoft office 365 dari control panel</p>\r\n\r\n<p>2.perbaiki system registry</p>\r\n\r\n<p>3.re instal MS Office</p>\r\n\r\n<p>4.Done</p>', 3, NULL, 6, '19:15:00', '19:15:00', '2021-05-20 12:20:35'),
(7, 'Memperbaikin printer yang tidak berfungsu', 16, 'user', 5, 0, 'Sering terjadi printer lantai tiga tidak bisa ngeprint', '<p><strong><strong>Cara</strong>&nbsp;Mengatasi&nbsp;<strong>Printer</strong>&nbsp;yang Tidak Bisa Mencetak</strong></p>\r\n\r\n<ol>\r\n	<li>Klik menu Start.</li>\r\n	<li>Selanjutnya pilih Control Panel.</li>\r\n	<li>Lalu pilih View Devices and&nbsp;<strong>Printers</strong>&nbsp;pada icon Hardware and Sound.</li>\r\n	<li>Kemudian akan muncul tampilan beberapa&nbsp;<strong>printer</strong>. Lalu klik kanan pada merek/jenis&nbsp;<strong>printer</strong>&nbsp;yang akan kamu gunakan. Dan pilih Set As Default Pinter.</li>\r\n	<li>Finish.</li>\r\n</ol>', 2, '1622077751_printer-error.png', 0, '08:15:00', '08:30:00', '2021-05-27 01:09:11'),
(8, 'Cheklist Laptop Baru', 20, 'user', 5, 0, 'Pengecekan Spesifikasi Laptop Baru', '<ol>\r\n	<li>Intel Core i5</li>\r\n	<li>RAM 8 GB</li>\r\n	<li>Display : 14&quot; FHD</li>\r\n	<li>Storage : SSD 512 GB</li>\r\n	<li>OS : Microsoft 10</li>\r\n	<li>Office : 365</li>\r\n	<li>Printer</li>\r\n	<li>PDF Reader</li>\r\n	<li>Anti Virus</li>\r\n	<li>Team Viewers</li>\r\n	<li>Zoom</li>\r\n	<li>E-Mail (Thunderbird/Outlook)</li>\r\n	<li>Barcode</li>\r\n</ol>', 2, '1622618692_Group 2.png', 0, '13:00:00', '14:30:00', '2021-06-02 07:24:53');
